INSERT INTO `soal` (`id`, `kategori`, `pendidikan`, `bidang_soal`, `pertanyaan`, `gambar_soal`, `opt_a`, `gambar_a`, `nilai_a`, `opt_b`, `gambar_b`, `nilai_b`, `opt_c`, `gambar_c`, `nilai_c`, `opt_d`, `gambar_d`, `nilai_d`, `opt_e`, `gambar_e`, `nilai_e`, `pembahasan`, `gbr_pembahasan`) VALUES
(1, 'BAGIAN I', 'SLTA', 'TWK', '<p>Pancasila disahkan oleh PPKI sebagai dasar filsafat negara Republik Indonesia pada tanggal<img xss=removed></p>\r\n', '', '<p>15 Agustus 1945</p>\r\n', '', 0, '<p>1 7 Agustus 1945</p>\r\n', '', 0, '<p>18 Agustus 1945</p>\r\n', '', 5, '<p>22 Juni 1945</p>\r\n', '', 0, '<p>26 Juni 1945</p>\r\n', '', 0, 'Sehari setelah kemerdekaan Indonesia pada tanggal 18 Agustus 1945 dalam sidang resmi PPKI, Pancasila dibahas, disempurnakan,danakhirnyadisahkan oleh PPKI sebagai dasar filsafat negara Republik Indonesia. Jawaban: C', ''),
(2, 'BAGIAN I', 'SLTA', 'TWK', '<p>Pancasila sebagai dasar negara pada hakikatnya telah menjadikan bangsa Indonesia ber-Pancasila dalam tiga prakarsa atau tiga asas, yaitu</p>\r\n', '', '<p>ketuhanan, kemanusiaan, persatuan</p>\r\n', '', 0, '<p>kemanusiaan, persatuan, kerakyatan</p>\r\n', '', 0, '<p>persatuan, kerakyatan, keadilan</p>\r\n', '', 0, '<p>kerakyatan, keadilan, kebudayaan</p>\r\n', '', 0, '<p>kebudayaan, religius, kenegaraan</p>\r\n', '', 5, 'Asas kebudayaan, secara yuridis Pancasila telah dimiliki oleh bangsa Indonesia dalam hal adat-istiadat dan kebudayaan.\r\nAsas religius, toleransi beragama yang didasarkan pada nilai-nilai religius telah mengakar kuat dalam kehidupan sehari-hari masyarakat Indonesia.\r\nAsas kenegaraan, karena Pancasila merupakan jati diri bangsa dan disahkan menjadi dasar negara, maka secara langsung bangsa Indonesia ber- Pancasila sebagai asas kenegaraan. \r\nJawaban: E', ''),
(3, 'BAGIAN I', 'SLTA', 'TWK', 'Ideologi secara umum adalah suatu kumpulan gagasan, ide, keyakinan, serta kepercayaan yang bersifat sistematis  yang  mengarahkan tingkah laku seseorang dalam berbagai bidang kehidupan. Sebagai ideologi, Pancasila termasuk ke dalam ideologi terbuka karena ...', '', 'nilai-nilai dan cita-cita dihasilkan dari pemikiran individu atau kelompok yang berkuasa dan masyarakatnya berkorban demi ideologinya', '', 0, 'menolak reformasi', '', 0, 'masyarakat harus taat kepada ideologi elite penguasa', '', 0, 'penguasa bertanggung jawab pada masyarakat sebagai pengemban amanat rakyat', '', 5, 'totaliter', '', 0, 'Ciri-ciri ideologi terbuka adalah:\n nilai-nilai dan cita-cita digali dari kekayaan adat istiadat, budaya, dan religius masyarakatnya\n menerima reformasi\n penguasa bertanggung jawab pada masyarakat sebagai pengemban amanat rakyat\njawaban: D', ''),
(4, 'BAGIAN I', 'SLTA', 'TWK', 'Pancasila sebagai suatu ideologi tidak bersifat kaku dan tertutup, namun bersifat reformatif, dinamis, dan terbuka. Hal ini dimaksudkan bahwa ideologi Pancasila adalah bersifat ....', '', 'aktual, statis, dan antisipatif', '', 0, 'aktual, dinamis, dan antisipatif', '', 0, 'faktual, statis, dan pragmatis', '', 0, 'faktual, dinamis, dan preventif', '', 5, 'aktual, statis, dan responsif', '', 0, 'Ideologi Pancasila bersifat aktual, dinamis, antisipatif, dan senantiasa mampu menyesuaikan dengan perkembangan zaman, ilmu pengetahuan dan teknologi, serta dinamika perkembangan aspirasi masyarakat. Keterbukaan ideologi Pancasila bukan berarti mengubah nilai-nilai dasar yang terkandung di dalamnya, namun mengeksplisitkan wawasannya secara lebih konkret sehingga memiliki kemampuan yang reformatif untuk memecahkan masalah-masalah aktual yang selalu berkembang.\njawaban: B', ''),
(5, 'BAGIAN I', 'SLTA', 'TWK', 'Ideologi Pancasila berdasar pada hakikat sifat kodrat manusia sebagai makhluk individu dan makhluk sosial. Oleh karena itu, ideologi Pancasila mengakui kebebasan hak- hak masyarakat. Kebebasan manusia dalam rangka demokrasi tidak melampaui hakikat nilai-nilai ....', '', 'ketuhanan', '', 5, 'kemanusiaan', '', 0, 'persatuan', '', 0, 'kerakyatan', '', 0, 'keadilan', '', 0, 'Manusia menurut Pancasila memiliki kodrat sebagai makhluk pribadi dan sebagai makhluk Tuhan Yang Maha Esa sehingga nilai-nilai ketuhanan senantiasa menjiwai kehidupan manusia dalam hidup bernegara dan bermasyarakat. Kebebasan manusia dalam rangka demokrasi tidak melampaui hakikat nilai-nilai ketuhanan. Bahkan, nilai ketuhanan terjelma dalam bentuk moral dalam ekspresi kebebasan manusia.\njawaban: A', ''),
(6, 'BAGIAN I', 'SLTA', 'TWK', 'Teks-teksyang termuat dalam konstitusi yang kemudian dikenal sebagai UUD 1945 adalah hasil perumusan apa yang terjadi di dalam kehidupan bermasyarakat. Keberadaan konstitusi dalam negara pada prinsipnya untuk', '', 'membatasi kekuasaan pemerintah', '', 5, 'memberikan kekuasaan tak terbatas pada pemerintah', '', 0, 'jaminan atas kewajiban politik rakyat', '', 0, 'kekuasaan parlemen  di  atas lembaga hukum', '', 0, 'kekuasaan hukum di  atas parlemen', '', 0, 'Makna konstitusi tidak sekadar naskah yang memuat ketentuan hukum dasar. Di dalamnya terkandung kehidupan sosial politik suatu bangsa yang menjadi pedoman bernegara. Keberadaan konstitusi dalam negara pada prinsipnya untuk membatasi kekuasaan pemerintah dan jaminan atas hak politik rakyat. Kekuasaan pemerintah diimbangi dengan kekuasaan parlemen dan Iembaga- lembaga hukum.\njawaban: A', ''),
(7, 'BAGIAN I', 'SLTA', 'TWK', 'Konstitusi Indonesia yang dikenal sebagai UUD 1945 dalam konteks klasifikasi konstitusi adalah konstitusi yang ....', '', 'tertulis, fleksibel, solid', '', 0, 'tidak tertulis, fleksibel, rigid', '', 0, 'tertulis, fleksibel, rigid', '', 5, 'tidak tertulis, toleran, rigid', '', 0, 'tertulis, toleran, solid', '', 0, 'Konstitusi Indonesia yang dikenal sebagai UUD 1945 dalam konteks klasifikasi konstitusi adalah konstitusi yang tertulis, fleksibel, dan rigid. Tertulis karena terdokumen dalam naskah, fleksibel karena hanya memuat 3Z pasal yang bisa mengikuti perkembangan zaman, dan rigid karena sulit cara merubahnya.\nJawaban: C', ''),
(8, 'BAGIAN I', 'SLTA', 'TWK', 'UUD 1945 sebagai hukum dasar Negara Kesatuan Republik Indonesia dipenuhi perdebatan yang sengit pada proses pembentukannya, terutama antara para founding fathers sebagai berikut, kecuali', '', 'Soepomo', '', 0, 'Muhammad Hatta', '', 0, 'Muhammad Yamin', '', 0, 'Soekarno', '', 0, 'H.O.S. Tjokroaminoto', '', 5, 'Sebagai negara yang baru merdeka pada tahun 1945, para founding fathers Indonesia telah merancangkan UUD 1945 sebagai hukum dasar Negara Kesatuan Republik Indonesia, walaupun tidak dapat dipungkiri bahwa pembentukannya dipenuhi perdebatan sengit, terutama antara Soepomo, Muhammad Hatta, Muhammad Yamin, dan Soekarno.\njawaban: E', ''),
(9, 'BAGIAN I', 'SLTA', 'TWK', 'Pada masa awal kemerdekaan, negara Indonesia belum mempunyai infrapolitik lengkap, yaitu Iembaga- lembaga negara yang mestinya ada sesuai ketentuan UUD 1945. Berdasarkan aturan peralihan pasal IV kekuasaan lembaga negara dijalankan oleh ...', '', 'perdana menteri', '', 0, 'presiden', '', 5, 'wakil presiden', '', 0, 'BPUPKI', '', 0, 'PPKI', '', 0, 'Berdasarkan aturan peralihan pasal IV, Sebelum Majelis Permusyawaratan Rakyat, Dewan Perwakilan Rakyat, dan Dewan Pertimbangan Agung dibentuk berdasarkan Undang-Undang Dasar ini, segala kekuasaannya dijalankan oleh presiden dengan bantuan sebuah komite nasional.\"\nJawaban: B', ''),
(10, 'BAGIAN I', 'SLTA', 'TWK', 'Problem awal dalam penyelenggaraan negarajustrumengingkari dibentuknya UUD 1945 yang berkeinginan adanya', '', 'stabilitas ekonomi', '', 0, 'swasembada pangan', '', 0, 'pemusatan kekuasaan', '', 0, 'pembatasan kekuasaan', '', 5, 'kontrol sosial', '', 0, 'Problem awal dalam penyelenggaraan negara justru mengingkari dibentuknya UUD 1945 yang berkeinginan adanya pembatasan kekuasaan, sebab UUD 1945 yang mestinya berpaham konstitusionalisme justru membuka peluang terjadi pemusatan kekuasaan kepada presiden dengan memberi kekuasaan segala fungsi negara, yaitu fungsi eksekutif, legislatif, dan pertimbangan kepada presiden.\njawaban: D', ''),
(11, 'BAGIAN I', 'SLTA', 'TWK', 'Berdasarkan wujud atau bentuknya, penggolongan tata hukum di Indonesia terdiri atas hukum tertulis dan hukum tidak tertulis. Berikut adalah contoh hukum tertulis, kecuali ....', '', 'hukum pidana', '', 0, 'hukum perdata', '', 0, 'hukum adat', '', 5, 'hukum dagang', '', 0, 'hukum tata usaha negara', '', 0, 'Hukum tertulis adalah hukum yang dicantumkan dalam bentuk peraturan perundang-undangan. Hukum tertulis ini  dapat  dibedakan  menjadi dua macam yaitu: hukum tertulis yang dikodifikasikan(hukumpidana, hukum perdata, hukum dagang, hukum tata usaha negara) dan hukum tertulis yang tidak dikodifikasikan (undang-undang, peraturan pemerintah, keputusan presiden kepres).\nJawaban: C', ''),
(12, 'BAGIAN I', 'SLTA', 'TWK', 'Hukum perdata adalah hukum yang mengatur hubungan antara orang yang satu dengan orang yang lain dengan menitikberatkan kepada kepentingan perseorangan. Ciri-ciri hukum perdata adalah sebagai berikut, kecuali', '', 'mengatur hubungan antara anggota masyarakat dengan negara yang mengatur tata tertib masyarakatnya', '', 5, 'mengatur hubungan antara orang yang satu dengan orang yang Iain', '', 0, 'mengatur hukum keluarga, hukum harta kekayaan, dan hukum waris', '', 0, 'korban berlaku sebagai penggugat', '', 0, 'tersangka berlaku sebagai tergugat', '', 0, 'Ciri- ciri hukum perdata:\n mengatur hubungan antara orang yang satu dengan orang yang lain\n mengatur hukum keluarga, hukum harta kekayaan, dan hukum waris\n proses pengadilan didasarkan\npada pengaduan dari pihak yang\nmerasa dirugikan (korban).\n korban berlaku sebagai penggugat\n tersangka berlaku sebagai\ntergugat.\nJawaban: A', ''),
(13, 'BAGIAN I', 'SLTA', 'TWK', 'Sumber hukum Indonesia dibagi menjadi dua, yaitu sumber hukum material dan sumber hukum formal. Berikut yang termasuk sumber hukum formal, kecuali', '', 'undang-undang', '', 0, 'kebiasaan', '', 0, 'keputusan-keputusan hakim', '', 0, 'traktat', '', 0, 'Pancasila', '', 5, 'Sumber hukum formal adalah tempat di mana dapat ditemukan aturan- aturan dan ketentuan-ketentuan mengenai hukum tertentu. Berikut yang termasuk sumber hukum formal:\n undang-undang (statute)\n kebiasaan (cus(om)\n keputusan-keputusan hakim\n(yurisprudentie)\n traktat (treaty)\n pendapat sarjana hukum (doktrin)\njawaban: E', ''),
(14, 'BAGIAN I', 'SLTA', 'TWK', 'Masalah-masalah yang menjadi jangkauan pengadilan tata usaha negara adalah sebagai berikut, kecuali', '', 'bidang ekonomi', '', 0, 'bidang politik', '', 0, 'bidang sosial', '', 0, 'bidang hak asasi manusia', '', 0, 'bidang umum', '', 0, 'Masalah-masalah yang menjadi jangkauan pengadilan tata usaha negara adalah sebagai berikut:  Bidangekonomi,yaitupermohonan yang berkaitan dengan perpajakan, hak merek, agraria.\n Bidang sosial, yaitu permohonan terhadap keputusan administrasi tentang penolakan permohonan suatu izin.\n Bidang hak asasi manusia, yaitu permohonan yang berkaitan dengan pencabutan hak milik seseorang serta penangkapan dan penahanan yang tidak sesuai dengan prosedur hukum.\n Bidang umum, yaitu permohonan\nyang berhubungan dengan status atau kedudukan seseorang seperti bidang kepegawaian  dan lain-lain.\nJawaban: B', ''),
(15, 'BAGIAN I', 'SLTA', 'TWK', 'Kekuasaan tidak berasal dari kekuasan lain yang lebih tinggi adalah pengertian dari sifat kedaulatan ....', '', 'asli', '', 5, 'abadi', '', 0, 'tunggal', '', 0, 'tidak terbatas', '', 0, 'terbatas', '', 0, 'Sifat kedaulatan menurut Jean Bodin\nsebagai berikut.\n Asli: kekuasaan tidak berasal dari kekuasaan lain yang lebih tinggi.  Abadi: kekuasaan itu tetap ada selama negara itu berdiri meskipun pemegangkedaulatannyaberganti- ganti.\n Tunggal: kekuasaan merupakan satu-satunya kekuasaan tertinggi dalam negarayangtidak diserahkan atau dibagi-bagi kepada badan lain.\n Tidak terbatas: kekuasaan itu tidak dapat dibatasi oleh kekuasaan lain. Jawaban: A', ''),
(16, 'BAGIAN I', 'SLTA', 'TWK', 'Sejalan dengan perubahan politik pemerintah kolonial, Wahidin Sudirohusodo, yang sejak tahun 1901 menjadi redaktur majalah Retnodhumilah, melalui majalahnya mempropagandakan pentingnya ....', '', 'kesehatan', '', 0, 'koperasi', '', 0, 'pendidikan', '', 5, 'politik', '', 0, 'militer', '', 0, 'Sejalan dengan perubahan  politik pemerintah kolonial yang hendak memajukan bidang pendidikan, dokter Wahidin Sudirohusodo yang sejak tahun 1901 menjadi redaktur majalah Retnodhumilah, melalui majalahnya ia mempropagandakan pentingnya pendidikan. Menurut pendapatnya bahwa pendidikan merupakan kunci kemajuan, diperlukan pendidikan secukupnya bagi kalangan luas orang pribumi. la mempropagandakan tentang pemberian beasiswa bagi pemuda-pemuda yang pandai tetapi tidak mampu.\njawaban: C', ''),
(17, 'BAGIAN I', 'SLTA', 'TWK', 'Pada tanggal 20 Mei 1908 di STOVIA (School not Opleiding voor Inlandsche ArtSen) oleh pelajar-pelajar di sana, didirikan organisasi bernama Budi Utomo dengan ketuanya adalah', '', 'Wahidin Sudirohusodo', '', 0, 'Sutomo', '', 5, 'Suraji', '', 0, 'Gunawan Mangunkusumo', '', 0, 'Pangeran Ario Noto Dirojo', '', 0, 'Sejak tahun 1906, Wahidin Sudirohusodo melakukan perjalanan berkeliling pulau Jawa untuk mempropagandakan cita-citanya. la yang didampingi Pangeran Ario Noto Dirojo, putra Paku Alam V, mula-mula mendekati para priyayi yang lebih tua dan lebih tinggi, khususnya bupati yang kaya dan berpengaruh. Pada akhir tahun 1907 dalam perjalanannya yang jauh, ia berhenti dan beristirahat di Jakarta. Ketika berada di Jakarta, ia diundang Sutomo dan  Suraji ke STOVIA (School too Opleiding voor InlandSche ArtSen) untuk mendengarkan gagasan-gagasannya. Demikianlah pada tanggal 20 Mei 1908 di STOVIA oleh pelajar-pelajar di sana didirikan organisasi bernama Budi Utomo dan Sutomo ditunjuk sebagai ketua. ]awaban: B', ''),
(18, 'BAGIAN I', 'SLTA', 'TWK', 'Ketua Indische Vereeniging, Nazir Datuk Pamuncak, menegaskan politik .... sebagai sendi perjuangan rakyat Indonesia.', '', 'etis', '', 0, 'divide et impera', '', 0, 'solidaritas', '', 0, 'swadaya', '', 0, 'nonkoperasi', '', 5, ' Ketua Indische Vereeniging Nazir Pamoncak menegaskan politik nonkoperasi sebagai sendi perjuangan rakyat Indonesia. Kerjasama dengan si penjajah untuk mencapai cita-cita kemerdekaan Indonesia, tidak Iain dari menipu diri sendiri. Kerjasama hanya mungkin antara dua golongan yang sama hak, kewajibannya, dan kepentingannya. Apabila syarat ini tidak dipenuhi, maka kerja sama berarti mempermainkan yang lemah oleh yang kuat, memperlakukan yang lemah sebagai alat untuk kepentingannya  sendiri.  Sebab itu IndiSche Vereeniging menolak kerjasama dan tetap menuju tujuan sendiri.\njawaban: E', ''),
(19, 'BAGIAN I', 'SLTA', 'TWK', 'Perkembangan Perhimpunan Indonesia menjadi organisasi politik terutama merupakan hasil usaha ....', '', 'Sutomo', '', 0, 'Iwa Kusumasumantri', '', 0, 'Nazir Datuk Pamuncak', '', 0, 'Muhammad Hatta', '', 5, 'Sukiman Wiryosanjoyo', '', 0, 'Perkembangan Perhimpunan Indonesia menjadi organisasi politik terutama merupakan hasil usaha Muhammad Hatta. Kegiatan Perhimpunan Indonesia diarahkan untuk mencapai tiga tujuan, pertama, menyadarkan mahasiswa agar semakin percaya merasa diri sebagai orang Indonesia. Kedua, Perhimpunan Indonesia harus berusaha menghapuskan gambaran tentang Indonesia yang diciptakan oleh pemerintah Belanda. Ketiga, yang terpenting adalah mereka harus mengembangkan ideologi yang kuat dan bebas dari pembatasan- pembatasan Islam dan komunisme.\njawaban: D', ''),
(20, 'BAGIAN I', 'SLTA', 'TWK', 'Pemberontakan PKI di Madiun, dipimpin oleh ....', '', 'Amir Syarifudin', '', 5, 'Kartosuwiryo', '', 0, 'Amir Fatah', '', 0, 'Teuku Daud Beureuh', '', 0, 'Kahar Muzakar', '', 0, 'Pemberontakan PKI di Madiun, dipimpin oleh Amir Syarifudin dan Muso. Pemberontakan ini merupakan pengkhianatan terhadap bangsa Indonesia.\nJawaban: A', ''),
(21, 'BAGIAN I', 'SLTA', 'TWK', 'Republik Demokrasi Timor Leste (RDTL), saat ini belum dikategorikan sebagai anggota ASEAN sepenuhnya RDTL masih diberikan status', '', 'spectator', '', 0, 'observer', '', 5, 'bystander', '', 0, 'watcher', '', 0, 'witness', '', 0, 'Republik Demokrasi Timor Leste (RDTL), saat ini belum dikategorikan sebagai anggota ASEAN sepenuhnya. RDTL masih diberikan status observer. RDTL harus mengajukan aplikasi resmi untuk menjadi anggota ASEAN, untuk kemudian aplikasi tersebut dibahas oleh negara-negara anggota ASEAN. Perkembangan terakhir, negara-negara ASEAN memberikan dukungannya agar RDTL bergabung sebagai anggota ASEAN.\n]awaban:  B', ''),
(22, 'BAGIAN I', 'SLTA', 'TWK', 'Sekretariat ASEAN berada di', '', 'Jakarta', '', 5, 'Singapura', '', 0, 'Kuala Lumpur', '', 0, 'Bangkok', '', 0, 'Manila', '', 0, 'Pada KTT ASEAN di Bali 1976, telah ditandatangani oleh para menteri luar negeri dengan disaksikan oleh para kepala pemerintahan yang kemudian dokumen perjanjian tersebut dikenal dengan nama Agreement  on the\n/St6bIiShment of the ASEAN Secretariat yang antara Iain menyatakan bahwa tempat kedudukan Sekretariat ASEAN di Jakarta, ibu kota negara RI.\nJawaban: A', ''),
(23, 'BAGIAN I', 'SLTA', 'TWK', 'Tokoh-tokoh yang memegang peran kunci proses pendirian Gerakan  Non Blok sejak awal adalah sebagai berikut, kecuali', '', 'Presiden Mesir, Gamal Abdel Nasser', '', 0, 'Presiden Ghana, Kwame Nkrumah', '', 0, 'Perdana Menteri India, Jawaharlal Nehru', '', 0, 'Presiden Indonesia, Sukarno', '', 0, 'Presiden Yugoslavia, Slobodan Milosevic', '', 5, 'KAA menyepakati Dasa Sila Bandung yang dirumuskan sebagai prinsip- prinsip dasar bagi penyelenggaraan hubungan dan kerja sama antara bangsa-bangsa. Sejak saat itu proses\npendirian GNB semakin mendekati kenyataan. Dalam proses ini tokoh- tokoh yang memegang peran kunci sejak awal adalah Presiden Mesir Gamal Abdel Nasser, Presiden Ghana Kwame Nkrumah, Perdana Menteri India Jawaharlal Nehru, Presiden Indonesia Soekarno, dan Presiden Yugoslavia Josip Broz Tito. Kelima tokoh dunia ini kemudian dikenal sebagai para pendiri GNB.\njawaban: E', ''),
(24, 'BAGIAN I', 'SLTA', 'TWK', 'Dalam beberapa tahun terakhir kegiatan yang menonjol antara Indonesia dan Colombo Plan adalah program pelatihan penanganan ....', '', 'banjir', '', 0, 'baby boom', '', 0, 'kemiskinan', '', 0, 'drug abuse', '', 5, 'tsunami', '', 0, 'Dalam beberapa tahun terakhir kegiatan yang menonjol antara Indonesia dan Colombo Plan adalah program pelatihan penanganan drug abuse yang dikordinasikan oleh Badan Narkotika Nasional bekerjasama dengan beberapa pesantren di Indonesia di bawah organisasi masyarakat Nahdlatul Ulama (NU).\njawaban: D', ''),
(25, 'BAGIAN I', 'SLTA', 'TWK', 'Indonesia telah terpilih sebagai anggota Dewan HAM PBB periode 2011-2014 dengan perolehan suara terbanyak yaitu 184 pada resumed session Sidang Majelis Umum PBB ke-65 di ....', '', 'Jakarta', '', 0, 'London', '', 0, 'New York', '', 5, 'Den Haag', '', 0, 'Kyoto', '', 0, 'Indonesia telah terpilih sebagai anggota Dewan HAM PBB periode 2011-2014 dengan perolehan suara terbanyak yaitu 184 pada resumed session Sidang Majelis Umum PBB ke-65 di New York, tanggal 20 Mei 2011. Sidang pemilihan dipimpin oleh Presiden Majelis Umum PBB dan 188 negara anggota memberikan suaranya. Selain Indonesia, terdapat 14 negara Iain yang terpilih sebagai anggota DHAM untuk periode 2011- 2014, yaitu: F ilipina, India, Kuwait, Republik Ceko, Rumania, Chili, Kosta Rika, Peru, Italia, Austria, Burkina Faso, Botswana, Kongo, dan Benin. Jawaban : C', ''),
(26, 'BAGIAN I', 'SLTA', 'TWK', 'Pembelajaran berbasis teknologi komputer perlu segera disosialisasikan sampai ke sekolah-sekolah di pelosok tanah air. Pernyataan tersebut terdapat dalam buku Pembelajaran Berbasis TIK halaman 32 karya Rahma Arif yang diterbitkan oleh Penerbit Fantasi pada tahun 2009. Teknik pengutipan teks tersebut yang benar adalah ....', '', 'Menurut Arif (2009:32) adalah Pembelajaran berbasis teknologi komputer perlu segera disosialisasikan sampai dengan sekolah-sekolah pelosok tanah air.', '', 0, 'Pembelajaran berbasis teknologi komputer perlu segera disosialisasikan sampai dengan sekolah-sekolah pelosok tanah air (Arif, 2009:32).', '', 5, 'Arif (2009:32) Pembelajaran berbasis teknologi komputer perlu segera disosialisasikan ke sekolah-sekolah di pelosok tanah air.', '', 0, 'Menurut Arif (2009:32) bahwa pembelajaran berbasis teknologi komputer perlu segera disosialisasikan ke sekolah- sekolah di pelosok tanah air.', '', 0, 'Pembelajaran berbasis teknologi komputer perlu segera disosialisasikan ke sekolah- sekolah di pelosok tanah air (dalam Arif, 2009:32).', '', 0, 'Teknik mengutip teks yang terdapat dalam buku harus memerhatikan urutan yang sesuai EBI. Nama pengarang yang lebih dari satu kata hanya ditulis nama belakangnya saja lalu diikuti tanda baca koma kemudian diikuti tahun terbit buku tersebut, tanda titik dua, serta halaman tempat pernyataan yang dikutip. Jadi, penulisan kutipan yang tepat adalah (Arif, 2009:32).\njawaban: B', ''),
(27, 'BAGIAN I', 'SLTA', 'TWK', 'Keanekaragaman hayati dan pemandangan .... bawah lautdi perairan Pulau   Lemukutan   yang  menjadi bagian dari Kawasan Konservasi Laut Daerah (KKLD) Bengkayang dalam setengah tahun terakhir makin ... wisatawan. KKLD Bengkayang berada di sekitar 35 kilometer sebelah barat Pulau Kalimantan di pesisir pantai Bengkayang. Di, situ terdapat ... pulau seperti Pulau Lemukutan, Randayan, Penata Besar, Penata Kecil, Baru, dan Kabung. Untuk mengisi titik-titik pada teks di atas, kata-kata yang tepat adalah ....', '', 'pesona, disenangi, kelompok', '', 0, 'indah, dikenal, bentangan', '', 0, 'elok, diketahui, banyak', '', 0, 'cantik, dikunjungi, deretan', '', 0, 'alam, diminati, gugusan', '', 5, 'Pilihan jawaban E, yakni kata alam, diminati, dam gugusan menjadikan kalimat wacana pada soal kohesif dan koherensif. Kata tersebut sesuai dan efektif untuk mengisi kata yang masih rumpang dalam paragraf.\njawaban: E', ''),
(28, 'BAGIAN I', 'SLTA', 'TWK', '<p>NASA selesai menguji coba sebuah sistem yang baru pertama kali diluncurkan, yaitu sebuah sistem internetantarplanet. jaringan ini akan dapat dipergunakan secara otomatis untuk menyebarluaskan informasi ke bumi, wahana antariksa, dan para astronaut. Wahana antariksa, biasanya, melakukan komunikasi langsung dengan bumi untuk memberikan instruksi kepada rover-rover Mars dalam mini eksplorasinya. Spirit dan Opportunity mentransmisikan data ke orbiter lalu diteruskan kembali ke bumi. Gagasan utama paragraf tersebut adalah ...</p>\r\n', '', '<p>NASA selesai menguji coba sistem komunikasi baru internet antarplanet.</p>\r\n', '', 5, '<p>jaringan internet antarplanet secara otomatis menyebarkan informasi.</p>\r\n', '', 0, '<p>Jaringan internet antarplanet tidak memerlukan bantuan tenaga manusia.</p>\r\n', '', 0, '<p>Wahana antariksa selalu melakukan komunikasi langsung dengan bumi.</p>\r\n', '', 0, '<p>Sistem baru internet antarplanet NASA bernama Spirit dan Opportunity.</p>\r\n', '', 0, 'Gagasan utama paragraf terdapat pada kalimat pertama (NASA selesai menguji coba sistem komunikasi baru internet antarplanet). Gagasan utama merupakan ide yang menjadi pokok pembahasan dalam paragraf.\r\njawaban: A', ''),
(29, 'BAGIAN I', 'SLTA', 'TWK', '<p>Pasar uang dan pasar modal Indonesia belum pulih. Rupiah masih terus mengalami tekanan mengikuti penurunan sebagian besar mata uang Asia. Masalah minimnya pasokan dolar AS di pasar valas semakin mempersulit mata uang lokal ini. Pada penutupan perdagangan valas tanggal 24 November 2008, rupiah melemah hingga 320 poin ke posisi 12.320 per dolar AS. Rupiah bahkan sempat menembus 12.325 per dolar AS. Pelaku pasar cemas karena belum ada sentimen positif dari dalam negeri. Oleh karena itu, pasar lebih memilih memegang dolar AS dalam kondisi pasar global yang sedang rentan ini. Paragraf di atas dikembangkan dengan menggunakan pola sebab-akibat karena ....</p>\r\n', '', '<p>kalimat utamanya menyatakan hubungan sebab dan akibat</p>\r\n', '', 0, '<p>kalimat pertama menyatakan sebab dan kalimat lainnya menyatakan akibat</p>\r\n', '', 0, '<p>kalimat ketiga menyatakan sebab, sedangkan kalimat lainnya menyatakan akibat</p>\r\n', '', 0, '<p>kalimat pertama dan ketujuh menyatakan akibat, sedangkan lainnya menyatakan sebab</p>\r\n', '', 0, '<p>kalimat ketujuh menyatakan akibat, sedangkan kalimat lainnya menyatakan sebab</p>\r\n', '', 5, 'Pada soal, paragraf dikembangkan berdasarkan pola sebab-akibat, yaitu keadaan pasar uang dan pasar modal Indonesia yang belum pulih mengakibatkan pelaku pasar cemas (kalimat 6) sehingga pasar lebih memilih dolar AS dalam kondisi pasar global yang sedang rentan ini (kalimat 7). jadi, kalimat ketujuh menyatakan akibat, sedangkan kalimat lainnya menyatakan sebab. Jawaban : E', ''),
(30, 'BAGIAN I', 'SLTA', 'TWK', 'Seseorang akan mengembangkan karangan yang bertema upaya mengatasi pergaulan bebas pada kaum remaja di perkotaan. Berdasarkan tema tersebut, topik-topik untuk kerangka karangan yang runtut adalah ..', '', 'Masalah yang timbul akibat pergaulan bebas pada kaum remaja di perkotaan, pentingnya mengatasi pergaulan bebas, langkah mengatasi pergaulan bebas, saran yang dapat dilakukan untuk mengatasi pergaulan bebas.', '', 5, 'Pentingnya mengatasi pergaulan bebas pada kaum remaja di perkotaan, masalah yang timbul akibat pergaulan bebas, langkah mengatasi pergaulan bebas, saran yang dapat dilakukan untuk mengatasi pergaulan bebas.', '', 0, 'Langkah mengatasi pergaulan bebas pada kaum remaja di perkotaan, pentingnya mengatasi pergaulan bebas, masalah yang timbul akibat pergaulan bebas, saran yang dapat dilakukan untuk mengatasi pergaulan bebas.', '', 0, 'Pentingnya mengatasi pergaulan bebas pada kaum remaja di perkotaan, langkah mengatasi pergaulan bebas, masalah yang timbul akibat pergaulan bebas, saran yang dapat dilakukan untuk mengatasi pergaulan bebas.', '', 0, 'Pentingnya saran yang dapat dilakukan untuk mengatasi pergaulan bebas di perkotaan, masalah yang timbul akibat pergaulan bebas, langkah mengatasi pergaulan bebas, saran yang dapat dilakukan untuk mengatasi pergaulan bebas.', '', 0, 'Topik-topik untuk kerangka karangan yang runtut adalah pilihan A. Pilihan ini sudah sistematis/runtut berdasarkan tema, yakni upaya mengatasi pergaulan bebas pada kaum remaja di perkotaan.\nJawaban: A', ''),
(31, 'BAGIAN I', 'SLTA', 'TIU', 'ABSEN = ', '', 'Bolos', '', 5, 'Hadir', '', 0, 'Tidak menentukan sikap', '', 0, 'Ragu-ragu', '', 0, 'Ada', '', 0, 'Absen - tidak masuk (sekolah, kerja,\ndsb); tidak hadir\nHadir - ada, datang\nTidak menentukan sikap abstain\nRagu-ragu - abstain Ada hadir; telah sedia\nJawaban: A', ''),
(32, 'BAGIAN I', 'SLTA', 'TIU', 'PENGUAPAN = ', '', 'Dehidrasi', '', 0, 'Rehidrasi', '', 0, 'Reboisasi', '', 0, 'Evaporasi', '', 5, 'Evakuasi', '', 0, 'Penguapan - proses, cara, perbuatan menguapi (menguapkan); kejadian menguap\nDehidrasi  kehilangan cairan tubuh Rehidrasi - proses senyawaan kembali Reboisasi - penghutanan kembali Evakuasi- pengungsian atau pemindahan penduduk dari daerah-daerah yang berbahaya\njawaban: D', ''),
(33, 'BAGIAN I', 'SLTA', 'TIU', 'ACUH > < ....', '', 'Peduli', '', 0, 'Tidak mau tahu', '', 5, 'Perhatian', '', 0, 'Istimewa', '', 0, 'Hirau', '', 0, 'Acuh - peduli; mengindahkan Peduli - acuh\nPerhatian - hal memperhatikan; apa yang diperhatikan; minat\nIstimewa - khas; khusus\nHirau - memedulikan; mengacuhkan; mengindahkan; memperhatikan\njawaban: B', ''),
(34, 'BAGIAN I', 'SLTA', 'TIU', 'ATROFI > < ....', '', 'Kalah', '', 0, 'Juara', '', 0, 'Pembesaran', '', 5, 'Piala', '', 0, 'Piagam', '', 0, 'Atrofi - penyusutan atau pengecilan ukuran suatu sel, jaringan, organ, atau bagian tubuh\nKalah - tidak menang atau dalam keadaan tidak menang\nsuara - orang (regu) yang mendapat kemenangan dalam pertandingan yang terakhir\nPiala - cawan berkaki dibuat dari emas, perak, dsb\nPiagam - surat resmi yang berisi pernyataan pemberian hak, tanah, dsb atau berisi pernyataan dan peneguhan mengenai suatu hal\njawaban: C', ''),
(35, 'BAGIAN I', 'SLTA', 'TIU', 'TANGAN : TUBUH = DAHAN : ....', '', 'Pohon', '', 5, 'Rindang', '', 0, 'Tangkai', '', 0, 'Daun', '', 0, 'Batang', '', 0, 'Tangan adalah bagian dari tubuh, sama halnya dahan adalah bagian dari pohon. jawaban: A', ''),
(36, 'BAGIAN I', 'SLTA', 'TIU', 'GENTENG : RUMAH = BENANG : .', '', 'Sutra', '', 0, 'Ulat', '', 0, 'Kapas', '', 0, 'Jahit', '', 0, 'Pakaian', '', 0, 'Genteng adalah salah satu bahan penyusun rumah, benang adalah salah satu bahan penyusun pakaian.\njawaban: E', ''),
(37, 'BAGIAN I', 'SLTA', 'TIU', 'KUMAN : STERIL = ISTRI : ', '', 'Orang tua', '', 0, 'Ibu', '', 0, 'Kakak', '', 0, 'Duda', '', 5, 'Suami', '', 0, 'Keadaan tanpa kuman disebut steril, keadaan tanpa istri disebut duda.\njawaban: D', ''),
(38, 'BAGIAN I', 'SLTA', 'TIU', 'Pilih jawaban yang tepat', '', 'Mobil Pribadi', '', 5, 'Bis', '', 0, 'Kereta Api', '', 0, 'Pesawat', '', 0, 'Angkot', '', 0, 'Merupakan kelompok alat transportasi masal. Mobil pribadi tidak termasuk alat transportasi masal.\njawaban: A', ''),
(39, 'BAGIAN I', 'SLTA', 'TIU', 'Pilih jawaban yang tepat', '', 'AC', '', 0, 'Kulkas', '', 0, 'Televisi', '', 0, 'Mesin Cuci', '', 0, 'Kompor Gas', '', 5, 'Merupakan kelompok peralatan listrik rumah tangga. Kompor gas tidak termasuk peralatan listrik.\njawaban: E', ''),
(40, 'BAGIAN I', 'SLTA', 'TIU', 'Pilih jawaban yang tepat', '', 'Pisang', '', 5, 'Mangga', '', 0, 'Jambu', '', 0, 'Apel', '', 0, 'Manggis', '', 0, 'Merupakan kelompok tumbuhan dikotil. Salah satu ciri tumbuhan dikotil adalah berbatang keras dan bercabang. Pohon pisang bukan termasuk tumbuhan dikotil.\njawaban: A', ''),
(41, 'BAGIAN I', 'SLTA', 'TIU', '<p>Bila seseorang berangkat bekerja pukul 06.10 menggunakan motornya dengan kecepatan tetap menempuh jarak 6 km dalam waktu 16 menit. Pada pukul berapa Dia sampai di kantornya bila jarak ke kantornya adalah 21 km?</p>\r\n', '', '<p>07,00</p>\r\n', '', 0, '<p>07,02</p>\r\n', '', 0, '<p>07,03</p>\r\n', '', 0, '<p>07,06</p>\r\n', '', 5, '<p>07,09</p>\r\n', '', 0, '', '3077b84af2950bc1d6e9808fb7fbb35a.png'),
(42, 'BAGIAN I', 'SLTA', 'TIU', '<p>jika dalam suatu kantor perbandingan antara pegawai wanita dan pegawai pria adalah 5 : 3, berapa persentase jumlah pegawai wanita pada kantor tersebut?</p>\r\n', '', '<p>25,5 %</p>\r\n', '', 0, '<p>32,5 %</p>\r\n', '', 0, '<p>48,5 %</p>\r\n', '', 0, '<p>56,5 %</p>\r\n', '', 0, '<p>62,5 %</p>\r\n', '', 5, '', '046a5044b8afed4025b72b9df72a04eb.png'),
(43, 'BAGIAN I', 'SLTA', 'TIU', '<p>Delapan pekerja bangunan membutuhkan waktu 12 hari untuk membangun pondasi gedung. Berapa tambahan pekerja yang dibutuhkan untuk membangun pondasi gedung dalam 2 hari?</p>\r\n', '', '<p>36</p>\r\n', '', 0, '<p>38</p>\r\n', '', 0, '<p>40</p>\r\n', '', 5, '<p>42</p>\r\n', '', 0, '<p>44</p>\r\n', '', 0, '', '316523bbbd335063ea842c1e45bb6125.png'),
(44, 'BAGIAN I', 'SLTA', 'TIU', '<p>Ahmad dan Beno mendaftar asuransi kesehatan dengan besaran premi yang sama. Apabila Ahmad yang menerima gaji sebesar Rp3.500.000 harus dipotong 5 untuk premi, berapa gaji Beno jika potongan preminya sebesar 4%?</p>\r\n', '', '<p>Rp3.750.000</p>\r\n', '', 0, '<p>Rp3.850.000</p>\r\n', '', 0, '<p>Rp4.150.000</p>\r\n', '', 0, '<p>Rp4.375.000</p>\r\n', '', 5, '<p>Rp4.750.000</p>\r\n', '', 0, '', '33519bde8080f0ab40e2a70de5175fb7.png'),
